function createRipple(e){const t=e.currentTarget,l=document.createElement("span"),n=Math.max(t.clientWidth,t.clientHeight),c=n/2;l.style.width=l.style.height=`${n}px`,l.style.left=`${e.clientX-t.offsetLeft-c}px`,l.style.top=`${e.clientY-t.offsetTop-c}px`,l.classList.add("ripple");const o=t.querySelector(".ripple");o&&o.remove(),t.appendChild(l)}const buttons=document.querySelectorAll("button[data-ripple]");for(const e of buttons)e.addEventListener("click",createRipple);