function createRipple(t){const e=t.currentTarget,l=document.createElement("span"),n=Math.max(e.clientWidth,e.clientHeight),o=n/2;l.style.width=l.style.height=`${n}px`,l.style.left=`${t.offsetX-o}px`,l.style.top=`${t.offsetY-o}px`,l.classList.add("ripple");const s=e.querySelector(".ripple");s&&s.remove(),e.appendChild(l)}const buttons=document.querySelectorAll("button[data-ripple]");for(const t of buttons)t.classList.contains("tooltip")||t.addEventListener("click",createRipple);